import React from 'react';

const ReservarTaller = () => {
  const styles = {
    container: {
      fontFamily: "'Century Gothic', 'Futura', sans-serif",
      backgroundImage: 'linear-gradient(to bottom right, #FAFAFA)',
    },
    header: {
      letterSpacing: '1.5px',
    },
    buttonHover: {
      transition: 'all 0.3s ease',
    }
  };

  return (
    <div 
      className="min-h-screen flex items-center justify-center p-4"
      style={styles.container}
    >
      {/* Contenedor principal */}
      <div 
        className="w-full max-w-md rounded-lg overflow-hidden shadow-2xl"
        style={{ border: '3px solid #065f46' }}
      >
        {/* Cabecera */}
        <div 
          className="py-4 px-6 text-center"
          style={{ backgroundColor: '#065f46' }}
        >
          <h1 
            className="text-2xl font-bold text-white uppercase"
            style={styles.header}
          >
            Reservar Taller
          </h1>
        </div>

        {/* Cuerpo */}
        <div className="bg-white p-6 space-y-6">
          <div className="space-y-3">
            <h2 
              className="text-xl font-semibold text-gray-800 pb-2"
              style={{ borderBottom: '2px solid #d1fae5' }}
            >
              Nombre del Taller
            </h2>
            <p className="text-gray-600 leading-relaxed">
              Descripción completa del taller con todos los detalles relevantes para el participante...
            </p>
          </div>

          {/* Botón principal */}
          <button
            className="w-full py-3 px-4 rounded text-white font-bold shadow-md"
            style={{
              backgroundColor: '#059669',
              ...styles.buttonHover
            }}
            onMouseEnter={(e) => e.target.style.backgroundColor = '#047857'}
            onMouseLeave={(e) => e.target.style.backgroundColor = '#059669'}
          >
            CONFIRMAR RESERVA
          </button>
        </div>
      </div>
    </div>
  );
};

export default ReservarTaller;
