import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function SignUp() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    nombre: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [errors, setErrors] = useState({});
  const [isRegistered, setIsRegistered] = useState(false);

  // Estilos en JS
  const styles = {
    signupContainer: {
      width: '350px',
      margin: '30px auto',
      padding: '30px',
      borderRadius: '15px',
      textAlign: 'center',
      fontFamily: 'Arial, sans-serif',
      backgroundColor: '#FAFAFA',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
    },
    logoContainer: {
      marginBottom: '25px'
    },
    logoText: {
      fontSize: '28px',
      fontWeight: '700',
      color: '#9316d2',
      margin: '0',
      letterSpacing: '1px'
    },
    logoSubtext: {
      fontSize: '18px',
      fontWeight: '500',
      color: '#555',
      margin: '5px 0 0 0',
      letterSpacing: '2px'
    },
    registerTitle: {
      fontSize: '20px',
      marginBottom: '25px',
      color: '#333333',
      fontWeight: '600',
      textTransform: 'uppercase'
    },
    formGroup: {
      marginBottom: '18px',
      textAlign: 'left'
    },
    formInput: {
      width: '100%',
      padding: '12px',
      border: '1px solid #666666',
      borderRadius: '8px',
      fontSize: '14px',
      transition: 'border 0.3s',
      boxSizing: 'border-box'
    },
    formInputFocus: {
      outline: 'none',
      borderColor: '#09ff00',
      boxShadow: '0 0 0 2px rgba(182, 84, 14, 0.1)'
    },
    registerButton: {
      width: '100%',
      padding: '12px',
      backgroundColor: '#D94F4F',
      color: '#FFF3E2',
      border: 'none',
      borderRadius: '8px',
      cursor: 'pointer',
      marginTop: '15px',
      fontWeight: '600',
      fontSize: '15px',
      transition: 'background-color 0.3s',
      textTransform: 'uppercase'
    },
    registerButtonHover: {
      backgroundColor: '#666666'
    },
    loginLink: {
      marginTop: '20px',
      cursor: 'pointer',
      color: '#666',
      fontSize: '14px'
    },
    loginLinkSpan: {
      color: '#666666',
      fontWeight: '500',
      textDecoration: 'underline'
    },
    error: {
      color: '#dc3545',
      fontSize: '12px',
      display: 'block',
      marginTop: '5px',
      textAlign: 'left'
    },
    successMessage: {
      textAlign: 'center',
      marginTop: '50px',
      padding: '30px',
      border: '1px solid #6b8e23',
      backgroundColor: '#FFF3E2',
      borderRadius: '10px',
      maxWidth: '400px',
      marginLeft: 'auto',
      marginRight: 'auto',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
    },
    successMessageH2: {
      color: '#6b8e23',
      marginBottom: '10px'
    },
    successMessageP: {
      color: '#333333',
      margin: '5px 0'
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!re.test(email)) {
      return 'Formato de correo inválido';
    }
    if (email === 'existente@ejemplo.com') {
      return 'Este correo ya está registrado';
    }
    return '';
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.nombre) newErrors.nombre = 'Nombre requerido';
    
    const emailError = validateEmail(formData.email);
    if (emailError) newErrors.email = emailError;
    
    if (formData.password.length < 8) {
      newErrors.password = 'La contraseña debe tener al menos 8 caracteres';
    }
    
    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Las contraseñas no coinciden';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log('Registro exitoso:', formData);
      setIsRegistered(true);
      setTimeout(() => {
        setFormData({
          nombre: '',
          email: '',
          password: '',
          confirmPassword: ''
        });
        setIsRegistered(false);
        navigate('/login');
      }, 3000);
    }
  };

  if (isRegistered) {
    return (
      <div style={styles.successMessage}>
        <h2 style={styles.successMessageH2}>¡Registro exitoso!</h2>
        <p style={styles.successMessageP}>Bienvenido a MasterCook Academy</p>
        <p style={styles.successMessageP}>Redirigiendo al login...</p>
      </div>
    );
  }

  return (
    <div style={styles.signupContainer}>
      <img src="/logo.png" alt="Logo" style={{ width: '150px', marginBottom: '20px' }} />
      <div style={styles.logoContainer}>
        {/* Puedes agregar aquí elementos adicionales si los necesitas */}
      </div>
      
      <h1 style={styles.registerTitle}>REGISTRO</h1>
      
      <form onSubmit={handleSubmit}>
        <div style={styles.formGroup}>
          <input
            type="text"
            name="nombre"
            value={formData.nombre}
            onChange={handleChange}
            placeholder="Nombre completo"
            style={styles.formInput}
            onFocus={(e) => e.target.style = { ...styles.formInput, ...styles.formInputFocus }}
            onBlur={(e) => e.target.style = styles.formInput}
          />
          {errors.nombre && <span style={styles.error}>{errors.nombre}</span>}
        </div>
        
        <div style={styles.formGroup}>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Correo electrónico"
            style={styles.formInput}
            onFocus={(e) => e.target.style = { ...styles.formInput, ...styles.formInputFocus }}
            onBlur={(e) => e.target.style = styles.formInput}
          />
          {errors.email && <span style={styles.error}>{errors.email}</span>}
        </div>
        
        <div style={styles.formGroup}>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            placeholder="Contraseña (mínimo 8 caracteres)"
            style={styles.formInput}
            onFocus={(e) => e.target.style = { ...styles.formInput, ...styles.formInputFocus }}
            onBlur={(e) => e.target.style = styles.formInput}
          />
          {errors.password && <span style={styles.error}>{errors.password}</span>}
        </div>
        
        <div style={styles.formGroup}>
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            placeholder="Confirmar contraseña"
            style={styles.formInput}
            onFocus={(e) => e.target.style = { ...styles.formInput, ...styles.formInputFocus }}
            onBlur={(e) => e.target.style = styles.formInput}
          />
          {errors.confirmPassword && <span style={styles.error}>{errors.confirmPassword}</span>}
        </div>
        
        <button 
          type="submit" 
          style={styles.registerButton}
          onMouseEnter={(e) => e.target.style.backgroundColor = styles.registerButtonHover.backgroundColor}
          onMouseLeave={(e) => e.target.style.backgroundColor = styles.registerButton.backgroundColor}
        >
          Registrarse
        </button>
      </form>
      
      <p 
        style={styles.loginLink} 
        onClick={() => navigate('/login')}
      >
        ¿Ya tienes cuenta? <span style={styles.loginLinkSpan}>Inicia Sesión</span>
      </p>
    </div>
  );
}

export default SignUp;
