// Components/Calendario.js
import React, { useEffect, useState } from "react";

const diasSemana = ["Dom", "Lun", "Mar", "Mié", "Jue", "Vie", "Sáb"];

const VerReservas = () => {
  const [reservas, setReservas] = useState([]);
  const [detalle, setDetalle] = useState(null);

  const hoy = new Date();
  const año = hoy.getFullYear();
  const mes = hoy.getMonth();
  const nombreMes = hoy.toLocaleString("es-ES", { month: "long" });

  const primerDiaSemana = new Date(año, mes, 1).getDay();
  const totalDias = new Date(año, mes + 1, 0).getDate();

  const dias = Array.from({ length: primerDiaSemana + totalDias }, (_, i) =>
    i < primerDiaSemana ? null : i - primerDiaSemana + 1
  );

  useEffect(() => {
    const data = JSON.parse(localStorage.getItem("reservas")) || [];
    setReservas(data);
  }, []);

  const formatoFecha = (dia) => {
    const d = new Date(año, mes, dia);
    return d.toISOString().slice(0, 10);
  };

  const handleCancelar = (fecha) => {
    const nuevas = reservas.filter((r) => r.fecha !== fecha);
    localStorage.setItem("reservas", JSON.stringify(nuevas));
    setReservas(nuevas);
    setDetalle(null);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6 flex flex-col items-center">
      <h1 className="text-2xl font-bold mb-4 capitalize">
        {nombreMes} {año}
      </h1>

      {/* Días de la semana */}
      <div className="grid grid-cols-7 gap-2 w-full max-w-2xl text-center font-semibold">
        {diasSemana.map((dia) => (
          <div key={dia}>{dia}</div>
        ))}
      </div>

      {/* Celdas de días */}
      <div className="grid grid-cols-7 gap-2 w-full max-w-2xl mt-2">
        {dias.map((dia, i) => {
          const fecha = dia ? formatoFecha(dia) : null;
          const reserva = reservas.find((r) => r.fecha === fecha);
          return (
            <div
              key={i}
              className={`h-20 border rounded-lg p-1 text-sm flex flex-col justify-between cursor-pointer ${
                reserva ? "bg-yellow-100" : "bg-white"
              }`}
              onClick={() => reserva && setDetalle(reserva)}
            >
              {dia && <span className="font-bold">{dia}</span>}
              {reserva && (
                <span className="text-xs text-blue-700">{reserva.taller}</span>
              )}
            </div>
          );
        })}
      </div>

      {/* Modal de detalle */}
      {detalle && (
        <div className="mt-6 bg-white p-4 rounded-xl shadow w-full max-w-md">
          <h3 className="font-bold mb-2">Reserva del {detalle.fecha}</h3>
          <p className="mb-2">Taller: {detalle.taller}</p>
          <button
            className="bg-red-500 text-white px-4 py-1 rounded"
            onClick={() => handleCancelar(detalle.fecha)}
          >
            Cancelar reserva
          </button>
        </div>
      )}
    </div>
  );
};

export default VerReservas;
