import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Pago = ({ taller, onPagoConfirmado }) => {
  const navigate = useNavigate();
  const [pagoExitoso, setPagoExitoso] = useState(false);
  const [procesando, setProcesando] = useState(false);

  // Paleta de colores MasterCook Academy
  const colors = {
    primary: '#D94F4F',    // Salsa Tomato
    secondary: '#FFF3E2',  // Creamy Vanilla
    accent: '#6B8E23',     // Olive Green
    textPrimary: '#333333', // Charcoal Gray
    textSecondary: '#666666', // Ash Gray
    background: '#FAFAFA'   // Snow White
  };

  const handleConfirmarPago = () => {
    setProcesando(true);
    
    // Simular llamada a API de pago
    setTimeout(() => {
      setPagoExitoso(true);
      setProcesando(false);
      
      // Actualizar estado de la reserva a "Pagado" después de 2 segundos
      setTimeout(() => {
        onPagoConfirmado();
      }, 2000);
    }, 1500);
  };

  const styles = {
    mainContainer: {
      minHeight: '100vh',
      backgroundColor: colors.background,
      display: 'flex',
      flexDirection: 'column',
      fontFamily: "'Century Gothic', Arial, sans-serif",
      padding: '20px'
    },
    card: {
      backgroundColor: 'white',
      borderRadius: '10px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
      padding: '25px',
      maxWidth: '500px',
      margin: '0 auto',
      width: '100%'
    },
    title: {
      color: colors.primary,
      textAlign: 'center',
      marginBottom: '25px',
      borderBottom: `2px solid ${colors.accent}`,
      paddingBottom: '10px'
    },
    sectionTitle: {
      color: colors.primary,
      marginBottom: '15px',
      fontSize: '18px'
    },
    infoRow: {
      display: 'flex',
      justifyContent: 'space-between',
      marginBottom: '10px',
      paddingBottom: '5px',
      borderBottom: `1px solid ${colors.secondary}`
    },
    infoLabel: {
      color: colors.textSecondary,
      fontWeight: '600'
    },
    infoValue: {
      color: colors.textPrimary,
      fontWeight: 'bold'
    },
    totalRow: {
      display: 'flex',
      justifyContent: 'space-between',
      margin: '20px 0',
      padding: '15px 0',
      borderTop: `2px solid ${colors.accent}`,
      borderBottom: `2px solid ${colors.accent}`
    },
    totalLabel: {
      color: colors.primary,
      fontSize: '18px',
      fontWeight: 'bold'
    },
    totalValue: {
      color: colors.primary,
      fontSize: '20px',
      fontWeight: 'bold'
    },
    paymentForm: {
      margin: '25px 0'
    },
    formGroup: {
      marginBottom: '15px'
    },
    formLabel: {
      display: 'block',
      marginBottom: '5px',
      color: colors.textPrimary,
      fontWeight: '600'
    },
    formInput: {
      width: '100%',
      padding: '12px',
      border: `1px solid ${colors.textSecondary}`,
      borderRadius: '8px',
      fontSize: '14px',
      boxSizing: 'border-box'
    },
    button: {
      backgroundColor: colors.accent,
      color: colors.secondary,
      border: 'none',
      borderRadius: '8px',
      padding: '14px',
      cursor: 'pointer',
      fontWeight: 'bold',
      fontSize: '16px',
      width: '100%',
      marginTop: '15px',
      transition: 'all 0.3s',
      textTransform: 'uppercase'
    },
    buttonDisabled: {
      backgroundColor: '#CCCCCC',
      cursor: 'not-allowed'
    },
    successMessage: {
      backgroundColor: '#F0FFF4',
      color: colors.accent,
      padding: '20px',
      borderRadius: '8px',
      marginBottom: '20px',
      textAlign: 'center',
      border: `1px solid ${colors.accent}`,
      fontSize: '16px'
    },
    backButton: {
      backgroundColor: colors.primary,
      color: colors.secondary,
      border: 'none',
      borderRadius: '8px',
      padding: '12px',
      cursor: 'pointer',
      fontWeight: 'bold',
      fontSize: '14px',
      width: '100%',
      marginTop: '15px',
      transition: 'all 0.3s'
    }
  };

  return (
    <div style={styles.mainContainer}>
      <div style={styles.card}>
        <h2 style={styles.title}>SIMULACIÓN DE PAGO</h2>
        
        {pagoExitoso ? (
          <>
            <div style={styles.successMessage}>
              ¡Pago confirmado exitosamente!<br />
              Tu reserva para "{taller.nombre}" ha sido completada.
            </div>
            
            <div style={styles.sectionTitle}>Resumen de la operación:</div>
            
            <div style={styles.infoRow}>
              <span style={styles.infoLabel}>Taller:</span>
              <span style={styles.infoValue}>{taller.nombre}</span>
            </div>
            <div style={styles.infoRow}>
              <span style={styles.infoLabel}>Fecha:</span>
              <span style={styles.infoValue}>{taller.fecha}</span>
            </div>
            <div style={styles.infoRow}>
              <span style={styles.infoLabel}>Estado:</span>
              <span style={{ ...styles.infoValue, color: colors.accent }}>Pagado</span>
            </div>
            
            <div style={styles.totalRow}>
              <span style={styles.totalLabel}>Total pagado:</span>
              <span style={styles.totalValue}>${taller.precio}</span>
            </div>
            
            <button
              style={styles.backButton}
              onClick={() => navigate('/verreservas')}
            >
              Ver mis reservas
            </button>
          </>
        ) : (
          <>
            <div style={styles.sectionTitle}>Resumen de la reserva:</div>
            
            <div style={styles.infoRow}>
              <span style={styles.infoLabel}>Taller:</span>
              <span style={styles.infoValue}>{taller.nombre}</span>
            </div>
            <div style={styles.infoRow}>
              <span style={styles.infoLabel}>Fecha:</span>
              <span style={styles.infoValue}>{taller.fecha}</span>
            </div>
            <div style={styles.infoRow}>
              <span style={styles.infoLabel}>Estado:</span>
              <span style={{ ...styles.infoValue, color: colors.primary }}>Reservado</span>
            </div>
            
            <div style={styles.totalRow}>
              <span style={styles.totalLabel}>Total a pagar:</span>
              <span style={styles.totalValue}>${taller.precio}</span>
            </div>
            
            <div style={styles.paymentForm}>
              <h3 style={styles.sectionTitle}>Información de pago</h3>
              
              <div style={styles.formGroup}>
                <label style={styles.formLabel}>Número de tarjeta</label>
                <input 
                  type="text" 
                  placeholder="1234 5678 9012 3456" 
                  style={styles.formInput}
                />
              </div>
              
              <div style={{ display: 'flex', gap: '15px' }}>
                <div style={{ ...styles.formGroup, flex: 1 }}>
                  <label style={styles.formLabel}>Fecha de expiración</label>
                  <input 
                    type="text" 
                    placeholder="MM/AA" 
                    style={styles.formInput}
                  />
                </div>
                <div style={{ ...styles.formGroup, flex: 1 }}>
                  <label style={styles.formLabel}>CVV</label>
                  <input 
                    type="text" 
                    placeholder="123" 
                    style={styles.formInput}
                  />
                </div>
              </div>
              
              <div style={styles.formGroup}>
                <label style={styles.formLabel}>Nombre en la tarjeta</label>
                <input 
                  type="text" 
                  placeholder="Nombre Apellido" 
                  style={styles.formInput}
                />
              </div>
            </div>
            
            <button
              style={{
                ...styles.button,
                ...(procesando && styles.buttonDisabled)
              }}
              onClick={handleConfirmarPago}
              disabled={procesando}
            >
              {procesando ? 'Procesando pago...' : 'Confirmar Pago'}
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default Pago;
