import React from 'react';

const ReservarTaller = () => {
  // Estilos en objeto para mejor organizaci贸n
  const styles = {
    mainContainer: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      minHeight: '100vh',
      backgroundColor: '#f8fafc',
      fontFamily: "'Century Gothic', Arial, sans-serif",
      padding: '20px'
    },
    card: {
      width: '100%',
      maxWidth: '450px',
      border: '3px solid #FFF3E2',
      borderRadius: '12px',
      overflow: 'hidden',
      boxShadow: '0 10px 25px -5px #FFF3E2'
    },
    header: {
      backgroundColor: '#FFF3E2',
      color: 'GREEN',
      padding: '20px',
      textAlign: 'center',
      textTransform: 'uppercase',
      letterSpacing: '1.2px'
    },
    content: {
      padding: '25px',
      backgroundColor: '#FAFAFA'
    }
  };

  return (
    <div style={styles.mainContainer}>
      {/* Tarjeta contenedora */}
      <div style={styles.card}>
        {/* Encabezado */}
        <div style={styles.header}>
          <h1 style={{ margin: 0, fontSize: '22px', fontWeight: 'bold' }}>
            RESERVAR TALLER
          </h1>
        </div>
        
        {/* Contenido */}
        <div style={styles.content}>
          <div style={{ marginBottom: '25px' }}>
            <h2 style={{
              fontSize: '18px',
              fontWeight: '600',
              color: '#333333',
              marginBottom: '10px',
              borderBottom: '#D94F4F',
              paddingBottom: '8px'
            }}>
              NOMBRE DEL TALLER
            </h2>
            <p style={{
              color: '#333333',
              lineHeight: '1.6',
              margin: 0
            }}>
              Descripci贸n completa del taller con todos los detalles...
            </p>
          </div>
          
          {/* Bot贸n */}
          <button
            style={{
              width: '100%',
              backgroundColor: '#D94F4F',
              color: '#FFF3E2',
              border: 'none',
              padding: '12px',
              borderRadius: '8px',
              fontWeight: 'bold',
              fontSize: '16px',
              cursor: 'pointer',
              transition: 'background-color 0.3s',
              textTransform: 'uppercase'
            }}
            onMouseEnter={(e) => e.target.style.backgroundColor = '#D94F4F'}
            onMouseLeave={(e) => e.target.style.backgroundColor = '#D94F4F'}
          >
            Confirmar Reserva
          </button>
        </div>
      </div>
    </div>
  );
};

export default ReservarTaller;
