import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import ComidaItaliana from './ComidaItaliana.jpg';
import ReposFrancesa from './ReposFrancesa.jpg';
import ComidaAsiatica from './ComidaAsiatica.jpg';

const Navegar = () => {
  const navigate = useNavigate();
  const [talleres, setTalleres] = useState([]);
  const [categorias, setCategorias] = useState([]);
  const [filtro, setFiltro] = useState("");
  const [cargando, setCargando] = useState(true);
  const [tallerExpandido, setTallerExpandido] = useState(null);

  useEffect(() => {
    setTimeout(() => {
      const datosEjemplo = [
        {
          id: 1,
          nombre: "Cocina Italiana",
          descripcion: "Aprende las técnicas tradicionales de pasta y salsas",
          categoria: "Internacional",
          fecha: "15/10/2023",
          precio: 120,
          cupos_disponibles: 5,
          cupos_totales: 15,
          imagen: ComidaItaliana
        },
        {
          id: 2,
          nombre: "Repostería Francesa",
          descripcion: "Domina los clásicos de la pastelería francesa",
          categoria: "Repostería",
          fecha: "20/10/2023",
          precio: 150,
          cupos_disponibles: 2,
          cupos_totales: 10,
          imagen: ReposFrancesa
        },
        {
          id: 3,
          nombre: "Cocina Asiática",
          descripcion: "Técnicas auténticas de cocina tailandesa y japonesa",
          categoria: "Internacional",
          fecha: "25/10/2023",
          precio: 135,
          cupos_disponibles: 8,
          cupos_totales: 12,
          imagen: ComidaAsiatica
        }
      ];

      setTalleres(datosEjemplo);
      setCategorias([...new Set(datosEjemplo.map(t => t.categoria))]);
      setCargando(false);
    }, 1000);
  }, []);

  const talleresFiltrados = filtro 
    ? talleres.filter(taller => taller.categoria === filtro)
    : talleres;

  const styles = {
    mainContainer: {
      minHeight: "100vh",
      backgroundColor: "#FAFAFA",
      display: "flex",
      flexDirection: "column",
      fontFamily: "'Century Gothic', Arial, sans-serif"
    },
    nav: {
      backgroundColor: "#D94F4F",
      padding: "15px 0",
      display: "flex",
      justifyContent: "center",
      flexWrap: "wrap"
    },
    navButton: {
      color: "#FFF3E2",
      background: "none",
      border: "none",
      fontWeight: "600",
      cursor: "pointer",
      padding: "8px 20px",
      margin: "0 10px",
      borderRadius: "20px",
      transition: "all 0.3s",
      fontSize: "14px",
      textTransform: "uppercase"
    }
  };

  const handleNavigation = (ruta) => {
    navigate(ruta);
  };

  return (
    <div style={styles.mainContainer}>
      <nav style={styles.nav}>
        <button
          style={styles.navButton}
          onClick={() => handleNavigation('/navegar')}
        >Talleres</button>
        <button
          style={styles.navButton}
          onClick={() => handleNavigation('/reservartaller')}
        >Reservar</button>
        <button
          style={styles.navButton}
          onClick={() => handleNavigation('/verreservas')}
        >Mis Reservas</button>
        <button
          style={styles.navButton}
          onClick={() => handleNavigation('/perfil')}
        >Perfil</button>
        <button
          style={styles.navButton}
          onClick={() => handleNavigation('/')}
        >Cerrar Sesión</button>
      </nav>

      {/* ...el resto del componente queda igual... */}
    </div>
  );
};

export default Navegar;
