import React from "react";
import { Link } from "react-router-dom";

const talleres = [
  {
    id: 1,
    nombre: "NOMBRE DEL TALLER",
    descripcion: "Descripción del taller...",
    imagen: "https://via.placeholder.com/80",
  },
  {
    id: 2,
    nombre: "NOMBRE DEL TALLER",
    descripcion: "Descripción del taller...",
    imagen: "https://via.placeholder.com/80",
  },
  {
    id: 3,
    nombre: "NOMBRE DEL TALLER",
    descripcion: "Descripción del taller...",
    imagen: "https://via.placeholder.com/80",
  },
  {
    id: 4,
    nombre: "NOMBRE DEL TALLER",
    descripcion: "Descripción del taller...",
    imagen: "https://via.placeholder.com/80",
  },
];

const Navegar = () => {
  return (
    <div className="min-h-screen bg-gray-100 flex flex-col justify-between">
      {/* Contenido principal */}
      <div className="p-4 flex-grow">
        <div className="bg-white w-full max-w-4xl mx-auto rounded-2xl border border-black p-6">
          {/* Título */}
          <h1 className="text-3xl font-bold text-center mb-8">TALLERES</h1>

          {/* Cuadrícula de tarjetas */}
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
            {talleres.map((taller) => (
              <div
                key={taller.id}
                className="flex items-center bg-white border rounded-2xl shadow p-4"
              >
                <img
                  src={taller.imagen}
                  alt="Imagen del taller"
                  className="w-20 h-20 object-cover rounded mr-4"
                />
                <div className="flex-1">
                  <h2 className="font-bold text-sm">{taller.nombre}</h2>
                  <p className="text-sm text-gray-600">{taller.descripcion}</p>
                </div>
                <button className="ml-4 bg-black text-white text-xs px-4 py-1 rounded-full">
                  + info
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Barra de navegación inferior */}
      <div className="w-full bg-white border-t shadow-md fixed bottom-0 left-0 flex justify-evenly items-center py-3 px-6 z-50">
        <Link to="/talleres">
        <img src="/icons/home.png" className="w-8 h-8" />
        </Link>
        <Link to="/reservar">
        <img src="/icons/book.png" className="w-8 h-8" />
        </Link>
        <Link to="/Mis reservas">
        <img src="/icons/calendar.png"  className="w-8 h-8" />
        </Link>
        <Link to="/perfil">
        <img src="/icons/user.png" className="w-8 h-8" />
        </Link>
      </div>
    </div>
  );
};

export default Navegar;
