import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function SignUp() {
  const navigate = useNavigate();
  const [showPassword, setShowPassword] = useState(false);
  const [formData, setFormData] = useState({
    nombre: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [errors, setErrors] = useState({});
  const [isRegistered, setIsRegistered] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  //decoracion de registro
  const styles = {
    body: {
      backgroundColor: '#FAFAFA',
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontFamily: 'Arial, sans-serif',
    },
    container: {
      width: '100%',
      maxWidth: '400px',
      padding: '2.5rem',
      borderRadius: '15px',
      backgroundColor: '#FFFFFF',
      boxShadow: '0 10px 25px rgba(0, 0, 0, 0.08)',
      border: '1px solid #eaeaea',
      transition: 'all 0.3s ease',
    },
    logo: {
      width: '120px',
      margin: '0 auto 1.5rem',
      display: 'block',
    },
    title: {
      color: '#333',
      textAlign: 'center',
      marginBottom: '1.8rem',
      fontSize: '1.5rem',
      fontWeight: '600',
    },
    formGroup: {
      marginBottom: '1.2rem',
      position: 'relative',
    },
    label: {
      display: 'block',
      marginBottom: '0.5rem',
      color: '#555',
      fontSize: '0.9rem',
      fontWeight: '500',
    },
    input: {
      width: '100%',
      padding: '0.8rem 1rem',
      border: '1px solid #ddd',
      borderRadius: '8px',
      fontSize: '0.95rem',
      transition: 'all 0.3s',
      backgroundColor: '#f9f9f9',
    },
    inputFocus: {
      borderColor: '#D94F4F',
      boxShadow: '0 0 0 3px rgba(217, 79, 79, 0.1)',
      backgroundColor: '#fff',
    },
    inputError: {
      borderColor: '#dc3545',
    },
    passwordContainer: {
      position: 'relative',
    },
    toggleButton: {
      position: 'absolute',
      right: '0.8rem',
      top: '50%',
      transform: 'translateY(-50%)',
      background: 'none',
      border: 'none',
      color: '#666',
      cursor: 'pointer',
      fontSize: '0.8rem',
      fontWeight: '600',
      padding: '0.2rem 0.5rem',
      borderRadius: '4px',
      transition: 'all 0.2s',
      ':hover': {
        color: '#D94F4F',
      },
    },
    submitButton: {
      width: '100%',
      padding: '0.8rem',
      marginTop: '1rem',
      backgroundColor: '#D94F4F',
      color: 'white',
      border: 'none',
      borderRadius: '8px',
      fontSize: '1rem',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'all 0.3s',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      ':hover': {
        backgroundColor: '#c03c3c',
        transform: 'translateY(-2px)',
      },
      ':active': {
        transform: 'translateY(0)',
      },
    },
    loadingSpinner: {
      marginRight: '0.5rem',
      animation: 'spin 1s linear infinite',
    },
    linkContainer: {
      textAlign: 'center',
      marginTop: '1.5rem',
      color: '#666',
      fontSize: '0.9rem',
    },
    loginLink: {
      color: '#D94F4F',
      fontWeight: '600',
      textDecoration: 'none',
      cursor: 'pointer',
      transition: 'all 0.2s',
      ':hover': {
        textDecoration: 'underline',
      },
    },
    errorMessage: {
      color: '#dc3545',
      fontSize: '0.8rem',
      marginTop: '0.3rem',
      display: 'block',
    },
    successContainer: {
      textAlign: 'center',
      padding: '2rem',
    },
    successIcon: {
      fontSize: '3rem',
      color: '#28a745',
      marginBottom: '1rem',
    },
    successTitle: {
      color: '#28a745',
      marginBottom: '0.5rem',
      fontSize: '1.3rem',
      fontWeight: '600',
    },
    successText: {
      color: '#555',
      marginBottom: '0.3rem',
    },
    '@keyframes spin': {
      '0%': { transform: 'rotate(0deg)' },
      '100%': { transform: 'rotate(360deg)' },
    },
  };

  // Manejo de eventos
  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Limpiar error cuando el usuario escribe
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(email) ? '' : 'Formato de correo inválido';
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.nombre.trim()) newErrors.nombre = 'Nombre requerido';
    if (!formData.email.trim()) newErrors.email = 'Correo requerido';
    else newErrors.email = validateEmail(formData.email);
    
    if (!formData.password) newErrors.password = 'Contraseña requerida';
    else if (formData.password.length < 8) newErrors.password = 'Mínimo 8 caracteres';
    
    if (!formData.confirmPassword) newErrors.confirmPassword = 'Confirma tu contraseña';
    else if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = 'Las contraseñas no coinciden';
    
    setErrors(newErrors);
    return Object.values(newErrors).every(error => !error);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;
    
    setIsLoading(true);
    
    try {
      // Simular llamada a API
      await new Promise(resolve => setTimeout(resolve, 1500));
      
      // En una aplicación real, aquí iría la llamada al backend:
      /*
      const response = await fetch('http://localhost:3001/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      
      if (!response.ok) throw new Error('Error en el registro');
      */
      
      setIsRegistered(true);
      
      // Redirigir después de 3 segundos
      setTimeout(() => {
        navigate('/login');
      }, 3000);
      
    } catch (error) {
      setErrors({
        submit: error.message || 'Error al registrar. Inténtalo de nuevo.'
      });
    } finally {
      setIsLoading(false);
    }
  };

  if (isRegistered) {
    return (
      <div style={styles.body}>
        <div style={styles.container}>
          <div style={styles.successContainer}>
            <div style={styles.successIcon}>✓</div>
            <h2 style={styles.successTitle}>¡Registro exitoso!</h2>
            <p style={styles.successText}>Bienvenido a MasterCook Academy</p>
            <p style={styles.successText}>Redirigiendo al login...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.body}>
      <div style={styles.container}>
        <img 
          src="/logo.png" 
          alt="Logo" 
          style={styles.logo} 
          onClick={() => navigate('/')}
        />
        
        <h2 style={styles.title}>Crear Cuenta</h2>
        
        <form onSubmit={handleSubmit}>
          {/* Campo Nombre */}
          <div style={styles.formGroup}>
            <label style={styles.label}>Nombre Completo</label>
            <input
              type="text"
              name="nombre"
              value={formData.nombre}
              onChange={handleChange}
              style={{
                ...styles.input,
                ...(errors.nombre && styles.inputError),
              }}
              onFocus={(e) => {
                e.target.style = { 
                  ...styles.input, 
                  ...styles.inputFocus,
                  ...(errors.nombre && styles.inputError)
                };
              }}
              onBlur={(e) => {
                e.target.style = { 
                  ...styles.input,
                  ...(errors.nombre && styles.inputError)
                };
              }}
              placeholder="Ingresa tu nombre completo"
            />
            {errors.nombre && <span style={styles.errorMessage}>{errors.nombre}</span>}
          </div>
          
          {/* Campo Email */}
          <div style={styles.formGroup}>
            <label style={styles.label}>Correo Electrónico</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              style={{
                ...styles.input,
                ...(errors.email && styles.inputError),
              }}
              onFocus={(e) => {
                e.target.style = { 
                  ...styles.input, 
                  ...styles.inputFocus,
                  ...(errors.email && styles.inputError)
                };
              }}
              onBlur={(e) => {
                e.target.style = { 
                  ...styles.input,
                  ...(errors.email && styles.inputError)
                };
              }}
              placeholder="tucorreo@ejemplo.com"
            />
            {errors.email && <span style={styles.errorMessage}>{errors.email}</span>}
          </div>
          
          {/* Campo Contraseña */}
          <div style={styles.formGroup}>
            <label style={styles.label}>Contraseña</label>
            <div style={styles.passwordContainer}>
              <input
                type={showPassword ? "text" : "password"}
                name="password"
                value={formData.password}
                onChange={handleChange}
                style={{
                  ...styles.input,
                  ...(errors.password && styles.inputError),
                  paddingRight: '70px',
                }}
                onFocus={(e) => {
                  e.target.style = { 
                    ...styles.input, 
                    ...styles.inputFocus,
                    ...(errors.password && styles.inputError),
                    paddingRight: '70px'
                  };
                }}
                onBlur={(e) => {
                  e.target.style = { 
                    ...styles.input,
                    ...(errors.password && styles.inputError),
                    paddingRight: '70px'
                  };
                }}
                placeholder="Mínimo 8 caracteres"
              />
              <button
                type="button"
                style={styles.toggleButton}
                onClick={togglePasswordVisibility}
              >
                {showPassword ? 'Ocultar' : 'Mostrar'}
              </button>
            </div>
            {errors.password && <span style={styles.errorMessage}>{errors.password}</span>}
          </div>
          
          {/* Campo Confirmar Contraseña */}
          <div style={styles.formGroup}>
            <label style={styles.label}>Confirmar Contraseña</label>
            <input
              type={showPassword ? "text" : "password"}
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              style={{
                ...styles.input,
                ...(errors.confirmPassword && styles.inputError),
              }}
              onFocus={(e) => {
                e.target.style = { 
                  ...styles.input, 
                  ...styles.inputFocus,
                  ...(errors.confirmPassword && styles.inputError)
                };
              }}
              onBlur={(e) => {
                e.target.style = { 
                  ...styles.input,
                  ...(errors.confirmPassword && styles.inputError)
                };
              }}
              placeholder="Vuelve a escribir tu contraseña"
            />
            {errors.confirmPassword && (
              <span style={styles.errorMessage}>{errors.confirmPassword}</span>
            )}
          </div>
          
          {/* Mensaje de error general */}
          {errors.submit && (
            <div style={{ ...styles.errorMessage, marginBottom: '1rem', textAlign: 'center' }}>
              {errors.submit}
            </div>
          )}
          
          {/* Botón de Registro */}
          <button
            type="submit"
            style={styles.submitButton}
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <span style={styles.loadingSpinner}>↻</span>
                Procesando...
              </>
            ) : (
              'Registrarse'
            )}
          </button>
        </form>
        
        <div style={styles.linkContainer}>
          ¿Ya tienes una cuenta?{' '}
          <span 
            style={styles.loginLink}
            onClick={() => navigate('/login')}
          >
            Inicia Sesión
          </span>
        </div>
      </div>
    </div>
  );
}

export default SignUp;
