import React from "react";

const talleres = [
  {
    id: 1,
    nombre: "NOMBRE DEL TALLER",
    descripcion: "Descripción del taller...",
    imagen: "https://via.placeholder.com/80",
  },
  {
    id: 2,
    nombre: "NOMBRE DEL TALLER",
    descripcion: "Descripción del taller...",
    imagen: "https://via.placeholder.com/80",
  },
  {
    id: 3,
    nombre: "NOMBRE DEL TALLER",
    descripcion: "Descripción del taller...",
    imagen: "https://via.placeholder.com/80",
  },
  {
    id: 4,
    nombre: "NOMBRE DEL TALLER",
    descripcion: "Descripción del taller...",
    imagen: "https://via.placeholder.com/80",
  },
];

const Talleres = () => {
  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
      <div className="bg-white w-full max-w-4xl rounded-2xl border border-black p-6 relative">
        {/* Título */}
        <h1 className="text-3xl font-bold text-center mb-8">TALLERES</h1>

        {/* Cuadrícula de tarjetas */}
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
          {talleres.map((taller) => (
            <div
              key={taller.id}
              className="flex items-center bg-white border rounded-2xl shadow p-4"
            >
              <img
                src={taller.imagen}
                alt="Imagen del taller"
                className="w-20 h-20 object-cover rounded mr-4"
              />
              <div className="flex-1">
                <h2 className="font-bold text-sm">{taller.nombre}</h2>
                <p className="text-sm text-gray-600">{taller.descripcion}</p>
              </div>
              <button className="ml-4 bg-black text-white text-xs px-4 py-1 rounded-full">
                + info
              </button>
            </div>
          ))}
        </div>

        {/* Barra de navegación inferior */}
        <div className="mt-10 flex justify-evenly items-center bg-white border rounded-full py-2 px-4 shadow-md">
          <img src="/icons/home.png" alt="Inicio" className="w-8 h-8" />
          <img src="/icons/book.png" alt="Talleres" className="w-8 h-8" />
          <img src="/icons/calendar.png" alt="Calendario" className="w-8 h-8" />
          <img src="/icons/user.png" alt="Perfil" className="w-8 h-8" />
        </div>
      </div>
    </div>
  );
};

export default Talleres;
